import React from 'react';
import { Nav } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { useLocation } from 'react-router-dom';

const Sidebar = () => {
  const { user } = useAuth();
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  if (!user) {
    return null; // Don't show sidebar for non-authenticated users
  }

  return (
    <div className="sidebar">
      <Nav className="flex-column p-3">
        <Nav.Item>
          <Nav.Link 
            href="/dashboard" 
            className={isActive('/dashboard') ? 'active' : ''}
          >
            üìä Dashboard
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link 
            href="/crime-map" 
            className={isActive('/crime-map') ? 'active' : ''}
          >
            üó∫Ô∏è Crime Map
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link 
            href="/barangays" 
            className={isActive('/barangays') ? 'active' : ''}
          >
            üèòÔ∏è Barangays
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link 
            href="/upload-crime-report" 
            className={isActive('/upload-crime-report') ? 'active' : ''}
          >
            üìù Upload Report
          </Nav.Link>
        </Nav.Item>
        <hr className="text-white" />
        <Nav.Item>
          <Nav.Link 
            href="/profile" 
            className={isActive('/profile') ? 'active' : ''}
          >
            üë§ Profile
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link 
            href="/audit-logs" 
            className={isActive('/audit-logs') ? 'active' : ''}
          >
            üìã Audit Logs
          </Nav.Link>
        </Nav.Item>
      </Nav>
    </div>
  );
};

export default Sidebar;
