import React, { useState, useEffect } from 'react';
import { Card, Container, Table, Button, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const Barangays = () => {
  const [barangays, setBarangays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    barangay_name: '',
    barangay_population: '',
    barangay_district: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchBarangays();
  }, []);

  const fetchBarangays = async () => {
    try {
      const response = await axios.get('/api/barangays');
      setBarangays(response.data);
    } catch (error) {
      console.error('Error fetching barangays:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/barangays', formData);
      setMessage('Barangay added successfully!');
      setShowModal(false);
      setFormData({ barangay_name: '', barangay_population: '', barangay_district: '' });
      fetchBarangays();
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error adding barangay');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Container fluid>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Barangays</h2>
        <Button variant="primary" onClick={() => setShowModal(true)}>
          Add New Barangay
        </Button>
      </div>

      {message && (
        <Alert variant="success" onClose={() => setMessage('')} dismissible>
          {message}
        </Alert>
      )}

      <Card>
        <Card.Body>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>Barangay Name</th>
                <th>Population</th>
                <th>District</th>
              </tr>
            </thead>
            <tbody>
              {barangays.map((barangay) => (
                <tr key={barangay.barangay_id}>
                  <td>{barangay.barangay_id}</td>
                  <td>{barangay.barangay_name}</td>
                  <td>{barangay.barangay_population}</td>
                  <td>{barangay.barangay_district}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      {/* Add Barangay Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Barangay</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Barangay Name</Form.Label>
              <Form.Control
                type="text"
                name="barangay_name"
                value={formData.barangay_name}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Population</Form.Label>
              <Form.Control
                type="number"
                name="barangay_population"
                value={formData.barangay_population}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>District</Form.Label>
              <Form.Control
                type="text"
                name="barangay_district"
                value={formData.barangay_district}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <div className="d-flex justify-content-end">
              <Button variant="secondary" className="me-2" onClick={() => setShowModal(false)}>
                Cancel
              </Button>
              <Button variant="primary" type="submit">
                Add Barangay
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Barangays;
