import React from 'react';
import { Card, Container, Row, Col, Button } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';

const Profile = () => {
  const { user } = useAuth();

  return (
    <Container fluid>
      <h2 className="mb-4">Profile</h2>
      <Row>
        <Col md={8}>
          <Card>
            <Card.Body>
              <Card.Title>User Information</Card.Title>
              <p><strong>Username:</strong> {user?.username || 'N/A'}</p>
              <p><strong>Email:</strong> {user?.email || 'N/A'}</p>
              <p><strong>Role:</strong> {user?.role || 'N/A'}</p>
              <Button variant="primary">Edit Profile</Button>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4}>
          <Card>
            <Card.Body>
              <Card.Title>Account Settings</Card.Title>
              <Button variant="outline-secondary" className="w-100 mb-2">Change Password</Button>
              <Button variant="outline-secondary" className="w-100 mb-2">Notification Settings</Button>
              <Button variant="outline-danger" className="w-100">Delete Account</Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
