import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import { Card, Container, Row, Col, Form, Button } from 'react-bootstrap';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default markers in React Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const CrimeMap = () => {
  const [crimes, setCrimes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    crimeType: '',
    barangay: '',
    dateRange: ''
  });

  useEffect(() => {
    fetchCrimeData();
  }, []);

  const fetchCrimeData = async () => {
    try {
      const response = await axios.get('/api/crimes');
      setCrimes(response.data);
    } catch (error) {
      console.error('Error fetching crime data:', error);
      // Mock data for demonstration
      setCrimes([
        {
          id: 1,
          type: 'Theft',
          description: 'Vehicle theft',
          latitude: 14.5995,
          longitude: 120.9842,
          barangay: 'Barangay A',
          date: '2024-01-15'
        },
        {
          id: 2,
          type: 'Assault',
          description: 'Physical assault',
          latitude: 14.6000,
          longitude: 120.9850,
          barangay: 'Barangay B',
          date: '2024-01-16'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const getMarkerColor = (crimeType) => {
    const colors = {
      'Theft': 'red',
      'Assault': 'orange',
      'Drugs': 'purple',
      'Fraud': 'blue',
      'Other': 'gray'
    };
    return colors[crimeType] || 'gray';
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const filteredCrimes = crimes.filter(crime => {
    if (filters.crimeType && crime.type !== filters.crimeType) return false;
    if (filters.barangay && crime.barangay !== filters.barangay) return false;
    return true;
  });

  if (loading) {
    return <div>Loading map...</div>;
  }

  return (
    <Container fluid>
      <h2 className="mb-4">Crime Map</h2>
      
      <Row>
        <Col md={3}>
          <Card>
            <Card.Body>
              <Card.Title>Filters</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Crime Type</Form.Label>
                  <Form.Select
                    name="crimeType"
                    value={filters.crimeType}
                    onChange={handleFilterChange}
                  >
                    <option value="">All Types</option>
                    <option value="Theft">Theft</option>
                    <option value="Assault">Assault</option>
                    <option value="Drugs">Drugs</option>
                    <option value="Fraud">Fraud</option>
                    <option value="Other">Other</option>
                  </Form.Select>
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Barangay</Form.Label>
                  <Form.Select
                    name="barangay"
                    value={filters.barangay}
                    onChange={handleFilterChange}
                  >
                    <option value="">All Barangays</option>
                    <option value="Barangay A">Barangay A</option>
                    <option value="Barangay B">Barangay B</option>
                    <option value="Barangay C">Barangay C</option>
                  </Form.Select>
                </Form.Group>
                
                <Button 
                  variant="outline-secondary" 
                  onClick={() => setFilters({ crimeType: '', barangay: '', dateRange: '' })}
                >
                  Clear Filters
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={9}>
          <Card>
            <Card.Body>
              <div className="map-container">
                <MapContainer
                  center={[14.5995, 120.9842]}
                  zoom={13}
                  style={{ height: '100%', width: '100%' }}
                >
                  <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                  />
                  
                  {filteredCrimes.map((crime) => (
                    <Marker
                      key={crime.id}
                      position={[crime.latitude, crime.longitude]}
                    >
                      <Popup>
                        <div>
                          <h6>{crime.type}</h6>
                          <p>{crime.description}</p>
                          <p><strong>Barangay:</strong> {crime.barangay}</p>
                          <p><strong>Date:</strong> {crime.date}</p>
                        </div>
                      </Popup>
                    </Marker>
                  ))}
                </MapContainer>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default CrimeMap;
